INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (49, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'El laberinto de los espíritus', 'Carlos Ruiz Zafón', 4.49, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1472484895l/31700489._SY475_.jpg', '"El laberinto de los espíritus", es el desenlace de la saga de "El cementerio de los libros olvidados" que se inició en 2001 con "La sombra del viento" y continuó en 2008 con "El juego del ángel" y en 2011 con "El prisionero del cielo".Elevadas por la crítica internacional a la categoría de clásico contemporáneo, las novelas de "El cementerio de los libros olvidados" se han convertido en uno de los universos literarios más apasionantes del nuevo siglo, y Carlos Ruiz Zafón en el escritor español más leído en todo el mundo después de Cervantes.', '9788408163381', 'Spanish');
