INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (84, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'Love★Com, Vol. 5', 'Aya Nakahara, Pookie Rolf (Translator)', 4.37, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1348677043l/1927111.jpg', 'Heartbroken by Ôtani''s rejection, Risa had decided to give up on ever getting together with him. But a chance encounter with her musical hero Umibôzu helps Risa get back in the fighting spirit. With a new surge of self-confidence, Risa is ready to go after Ôtani. He''ll never know what hit him.', '9781421517384', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (86, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'The Talisman and Black House', 'Stephen King (Goodreads Author), Peter Straub (Goodreads Author)', 4.45, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1323105529l/97126.jpg', 'Stephen King and Peter Straub. LIMITED/SIGNED BY STRAUB AND BERRY. The Talisman and Black House. Hampton Falls: Donald M. Grant, 2003. First edition of the limited collection. Limited to 3,500 numbered and signed sets. Signed by Straub and the illustrator, Rick Berry on the limitation page. Publisher''s binding, dust jackets, and slipcase. Donald M. Grant, Publisher, is proud to announce the publication of a new limited edition of THE TALISMAN and of its sequel, BLACK HOUSE by authors Stephen King and Peter Straub. Both books have 11 new full color plates, numerous black and white devices and are also designed by award winning artist Rick Berry. The bindings of both books are black leatherette stamped with two colors of foil and are available only as a set in a single foil stamped slipcase. The set is limited to 3,500 numbered copies. THE TALISMAN in each set is signed by author Peter Straub and artist Rick Berry. retail price is $150.00.', '9781880418536', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (87, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'The Word of Promise Complete Audio Bible: NKJV', 'Anonymous, Marcia Gay Harden (narrator), Stacy Keach (narrator), Malcom McDowell (narrator), Jon Voight (narrator), Gary Sinese (narrator), Max von Sydow (narrator), Marisa Tomei (narrator), more…', 4.55, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1306255239l/11448077.jpg', 'This faithful rendering of the New King James Version presents the Bible in more than 90 hours of compelling, dramatic audio theater format. This world-class audio production immerses listeners in the dramatic reality of the Scriptures as never before, with an original music score by composer Stefano Mainetti (Abba Pater), feature-film quality sound effects, and compelling narration by Michael York and the work of over 500 actors.Each beloved book of the Bible comes to life with outstanding performances by Jim Caviezel as Jesus, Richard Dreyfuss as Moses, Gary Sinise as David, Jason Alexander as Joseph, Marisa Tomei as Mary Magdalene, Stacy Keach as Paul, Louis Gossett, Jr. as John, Jon Voight as Abraham, Marcia Gay Harden as Esther, Joan Allen as Deborah, Max von Sydow as Noah, and Malcolm McDowell as Solomon.98 hours', 'B003UC45I8', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (88, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'The Compact Edition of the Oxford English Dictionary, 2 Vols w/Reading Glass', 'Herbert Coleridge (editor), Frederick J. Furnivall (editor), James Murray (editor), Charles Talbut Onions (editor)', 4.62, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1209136480l/441499.jpg', 'Linguistic type: lexicon. Complete text (without the four Supplement volumes) reproduced micrographically of the 13 volume Oxford English Dictionary published in 1933.
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (89, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'The Fixed Trilogy', 'Laurelin Paige (Goodreads Author)', 4.35, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1394517753l/21420473.jpg', 'All three books of the NY Times Bestselling Fixed Trilogy are included in this bundle.Fixed on You (Book 1):  Stalking and restraining orders are a thing of Alayna Wither''s past. With her MBA newly in hand, she has her future figured out--move up at the nightclub she works at and stay away from any guy who might trigger her obsessive love disorder. A perfect plan. But what Alayna didn''t figure on is Hudson Pierce, the new owner of the nightclub. He''s smart, rich, and gorgeous--the kind of guy Alayna knows to stay away from if she wants to keep her past tendencies in check. Except, Hudson''s fixed his sights on her. He wants her in his bed and makes no secret of it. Avoiding him isn''t an option after he offers a business proposition she can''t turn down and she''s drawn further into his universe, unable to resist his gravitational pull. When she learns Hudson has a dark history of his own, she realizes too late that she''s fallen for the worst man she could possibly get involved with. Or maybe their less than ideal pasts give them an opportunity to heal each other and finally find the love their lives have been missing.Found in You (Book 2): Alayna Withers has only had one kind of relationship: the kind that makes her obsessive and stalker-crazy. Now that Hudson Pierce has let her into his heart, she''s determined to break down the remaining walls between them so they can build a foundation that''s based on more than just amazing sex. Except Hudson''s not the only one with secrets. With their pasts pulling them into a web of unfounded mistrust, Alayna turns to the one person who knows Hudson the best--Celia, the woman he almost married. Hoping for insight from someone who understands all sides of the story, Alayna forms a bond with Celia that goes too far--revealing things about Hudson that could end their love for good.This is the first relationship where Alayna hasn''t spiraled out of control. And she might lose Hudson anyway...Forever with You (Book 3): Alayna Withers relationship with Hudson Pierce has tested both her and his ability to trust. They decide that the only way they can move forward together is with open doors and transparency. It won''t be easy for the scarred lovers, but they''re committed to each other more now than ever. Alayna, in particular, has grown through their trials, and has emerged more confident and faithful to the man she loves.But while the pair is focused on the future, their past shows up again to threaten their fragile bond. Promises that were made are broken, and Alayna learns that Hudson still has very potent secrets--secrets that will tear them both apart. As much as she feels for him, her ability to forgive and forget is tested beyond her limits.Even though she found the only man who could fix her, a forever with Hudson seems more and more out of reach.', 'B00IX9UTLU', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (90, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'The Unwanteds Complete Collection', 'Lisa McMann (Goodreads Author)', 4.73, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1446397947l/26029281._SX318_.jpg', 'Experience the magic of Artimé and the chills of Quill with all seven fantastical books in the New York Times bestselling Unwanteds series, now available in a collectible boxed set!“The Hunger Games meets Harry Potter” (Kirkus Reviews) in this thrilling series collected here in a hardcover boxed set featuring all seven books.This captivating boxed set includes The Unwanteds, Island of Silence, Island of Fire, Island of Legends, Island of Shipwrecks, Island of Graves, and The Unwanteds #7.', '9781481468862', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (91, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'Bastard', 'Youngchan Hwang, CarnKim (Illustrator)', 4.51, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1490595397l/33987008.jpg', 'Jin Seong is weak, bullied and practically invisible at school. Because of his particular situation, he is forced to commit murder.', '9999999999999', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (92, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'Beginnings: Obsidian & Onyx', 'Jennifer L. Armentrout (Goodreads Author)', 4.57, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1405516251l/19081403.jpg', 'Now available together for the first time, don''t miss Obsidian and Onyx, the first two books in Jennifer L. Armentrout''s bestselling Lux series. Also includes bonus content exclusive to the print edition! "A thrilling ride from start to finish," says RT Book Reviews.ObsidianThere’s an alien next door. And with his looming height and eerie green eyes, he’s hot… until he opens his mouth. He’s infuriating. Arrogant. Stab-worthy. But when a stranger attacks me and Daemon literally freezes time with a wave of his hand, he marks me. Turns out he has a galaxy of enemies wanting to steal his abilities and the only way I’m getting out of this alive is by sticking close to him until my alien mojo fades. If I don’t kill him first, that is.OnyxDaemon’s determined to prove what he feels for me is more than a product of our bizarro connection. So I’ve sworn him off, even though he’s running more hot than cold these days. But we’ve got bigger problems. I’ve seen someone who shouldn’t be alive. And I have to tell Daemon, even though I know he’s never going to stop searching until he gets the truth. What happened to his brother? Who betrayed him? And what does the DOD want from them—from me?', '9781622664481', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (93, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'The Secret Behind the Cross and Crucifix', 'Nwaocha Ogechukwu (Goodreads Author)', 4.50, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1348037767l/7490651.jpg', 'If you''re a Dan Brown fan, you''ll want to read, The Secret Behind the Cross and Crucifix. Author Nwaocha Ogechukwu has written an easy to read, enlightening and academically sound book regarding the symbolism and meaning of the cross in relation to religion. Ogechukwu gives historical accounts of Christianity''s cover up of what the cross truly is: a satanic symbol. "For centuries after Christ, the church and other religions that use cruciform symbols have misrepresented the physical nature of Christ''s death with a satanic symbol (cross), and a pagan idol (corpus). This secret has been concealed by the church for centuries after Christ." Ogechukwu''s research leads to a stunning conclusion as it explores to understand the real nature of Christ''s death, religion''s role in the symbolism, and to release humankind from the "painful knowledge bondage" of cruciform propaganda. Nwaocha Ogechukwu is a graduate of medical science, member of the Royal Institute of Philosophy, and a researcher in philosophy, religion, history, and psychology. Nominated as "One of the Great Minds of the 21st Century," by the American Biographical Institute, Mr. Ogechukwu lives in Nigeria. The Secret Behind the Cross and Crucifix is his second novel. He is currently working on his third book and fourth books.', '9781606933671', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (100, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'Resisting Temptation', 'K.C. Lynn (Goodreads Author)', 4.54, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1518115064l/25025389._SX318_.jpg', 'Faith ~  INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (94, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'Button Finds Family and Friends at the Farm', 'Joyce Mitchell (Goodreads Author)', 4.62, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1420300894l/24277991.jpg', 'When Button visits a farm, he wants Farmer Chris to take him up the hill to a cute little red hen house for their first stop. A rooster pecks his toe and he rolls down the hill, crushing watermelons along the way. Unhurt, he finds a big surprise at the bottom of the hill near Sam’s Sheep Farm. What is the surprise? Share the love of reading with your beginner reader!', '9781505664911', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (95, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'Becoming A Vincent', 'C.M. Owens (Goodreads Author)', 4.36, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1498002051l/35487055._SX318_.jpg', 'Alternative Cover Edition of ASIN B072YXCNK1When you live in a place where “turbo speed” internet is a slight step above dial-up, men carry on nine-year beard-growing challenges out of stubborn pride, and your brothers do things like nail all your panties to the outside of your cabin just for funsies, you tend to be a little crazy. You can call it a "locational" hazard, if you will.That’s Tomahawk for you.We rank people based on just how crazy they are. And the four craziest families in town are called the Wild Ones. I’m on the bottom tier of those, so technically I’m not as crazy as the other Wild Ones. In fact, if it wasn’t for my brothers and their endless antics, I wouldn’t be considered a Wild One at all. Ahem. Sure. We’ll go with that.Anyway, I have a best friend who endures it all with me. Benson Nolans is my one, constant favorite person.Without him, I’d probably go really crazy, and not the fun kind. It’d be ridiculous, after three years of a flawless friendship, to mess that all up by falling for him.I mean, even if we did get a little too close one night, it’d be reckless endangerment. Even if we did suddenly feel the chemistry that’s always been there and stop toeing the line, it’d be a foolish risk to take.It’d be stupid to start hoping a really fun, but completely irrational, night with zero inhibitions might accidentally happen.Really stupid…  Right?*NO cliffhanger*Stand-alone book *Sexual Content*Adult language*Completely, 100% crazy', '9999999999999', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (96, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'The War on Women', 'Sue Lloyd-Roberts', 4.74, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1470870148l/31422889._SY475_.jpg', 'Sue Lloyd-Roberts joined ITN in 1973 as a news trainee, she went on to become the UK''s first female video-journalist, reporting alone from the bleak outposts of the former Soviet Union and China. With a 30-year-long career in human-rights journalism, she has travelled the globe and witnessed the worst atrocities inflicted on women. Observing first-handthe war on the female race, she''s experienced and interacted with the brave ones who fight back. This is a breath-taking and visceral narrative, interweaving the real-life experiences of the heroines combating gross inequality. It is an examination of how women are treated across the globe: from the pay gap in the UK and the laundries in Ireland, to gender discrimination in Saudi Arabia and female genital mutilation in Africa. In a world where the issues facing women are so disparate, we''re facing a war of varying extremities and this has created a breakdown in the feminist discourse. But through her extraordinary and unique experiences, Lloyd-Roberts starts to build a bigger picture with a pervasive perspective. The book delves into our history and takes us on a journey towards the analysis of the state of women''s lives in modern-day society. This is a ground-breaking approach to a global problem; anecdotal evidence bridges the gap between different fights and gradually starts to knit together the battles being fought by the starkly different cultures across the world.', '9781471153914', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (97, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'King Series Bundle: The Four Book Set', 'T.M. Frazier (Goodreads Author)', 4.68, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1471217056l/31446987._SY475_.jpg', 'All four books in the USA today bestselling KING series. King, Tyrant, Lawless, Soulless.  KING: Homeless. Hungry. Desperate. Doe has no memories of who she is or where she comes from. A notorious career criminal just released from prison, King is someone you don’t want to cross unless you’re prepared to pay him back in blood, sweat, sex or a combination of all three. King’s future hangs in the balance. Doe’s is written in her past. When they come crashing together, they will have to learn that sometimes in order to hold on, you have to first let go.  TYRANT: I. Remember. Everything. Only now I wish I didn’t. When the fog is sucked away from my mind like smoke through a vacuum, the truth that has been beyond my reach for months finally reveals itself. But the relief I thought I would feel never comes, and I’m more afraid now than I was the morning I woke up handcuffed in King’s bed. Because with the truth comes dark secrets I was never meant to know. I will put the lives of those I love most at risk if I let on that my memory has returned, or if I seek help from the heavily tattooed felon who owns me body and soul. I don’t know if I’m strong enough to resist the magnetic pull toward King that grows stronger every day. He’s already saved me in more ways than one. Now it’s my turn to do whatever it takes to save him. Even if that means marrying someone else…  LAWLESS: She''s prim and proper. He''s leather and lawless. It will never work between them. It''s a lie they almost believe.  SOULLESS: The conclusion to Bear and Thia''s epic love story.', 'B01KB7P7UO', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (98, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'J.R.R. Tolkien 4-Book Boxed Set: The Hobbit and The Lord of the Rings', 'J.R.R. Tolkien', 4.60, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1346072396l/30.jpg', 'This four-volume, boxed set contains J.R.R. Tolkien''s epic masterworks The Hobbit and the three volumes of The Lord of the Rings (The Fellowship of the Ring, The Two Towers, and The Return of the King).In The Hobbit, Bilbo Baggins is whisked away from his comfortable, unambitious life in Hobbiton by the wizard Gandalf and a company of dwarves. He finds himself caught up in a plot to raid the treasure hoard of Smaug the Magnificent, a large and very dangerous dragon.The Lord of the Rings tells of the great quest undertaken by Frodo Baggins and the Fellowship of the Ring: Gandalf the wizard; the hobbits Merry, Pippin, and Sam; Gimli the dwarf; Legolas the elf; Boromir of Gondor; and a tall, mysterious stranger called Strider. J.R.R. Tolkien''s three volume masterpiece is at once a classic myth and a modern fairy tale—a story of high and heroic adventure set in the unforgettable landscape of Middle-earth', '9780345538376', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (99, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'Masters of the Shadowlands', 'Cherise Sinclair (Goodreads Author)', 4.51, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1542188593l/6734445.jpg', '"Club Shadowlands" Blundering into a BDSM club, accountant Jessica attracts the owner''s attention. When Master Z discovers her interest in BDSM, he can''t resist tying her up and unleashing the passion she hides within."Dark Citadel" After Kari breaks up with her date during Beginner''s Night at Club Shadowlands, she''s given the option to continue with an experienced dominant. Under Master Dan''s guidance, she learns the true meaning of submission... can she teach him to love?Publisher''s Note: This book contains explicit sexual content, graphic language, and situations some readers may find objectionable: Anal play/intercourse, BDSM theme and content (including/not limited to bondage, caning, restraint, spanking, tethering) exhibitionism, voyeurism.', '9781596328792', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (101, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'Following the Sun', 'Alisa Mullen (Goodreads Author)', 4.60, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1417413020l/23638035.jpg', 'Nate Wilson lives his life with pride and heart. When his pops hands down the family bowling alley in Framingham, Massachusetts, he doesn''t think twice about making it his own. Nate is a guy''s guy. He is true to his old friends, his family, his dates, and his business. Sunny, a vivacious and mysterious woman frequents the alley all of a sudden. Somehow she catches Nate''s attention and holds it. Nate becomes slightly obsessed with who she is and why she loves the bowling alley.He is not one to be easily distracted by a woman to the point of letting his priorities fall the wayside.However, after he follows his instinct, Nate finds himself entranced, enthralled, and in the worst predicament of his steady life.', '9999999999999', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (102, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'Lawless & Soulless', 'T.M. Frazier (Goodreads Author)', 4.69, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1528314889l/30333711._SY475_.jpg', 'Lawless and Soulless together in one book! Books 3&4 in the USA TODAY BESTSELLING KING SERIES. Lawless: She''s prim and proper. He''s leather and lawless. It will never work between them. It''s a lie they almost believe. Soulless: The conclusion to Bear and Thia''s epic love story.', 'B01FOIRTZQ', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (103, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'Fullmetal Alchemist, Vol. 2', 'Hiromu Arakawa, Akira Watanabe (Translator)', 4.52, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1348990451l/873.jpg', 'In an alchemical ritual gone wrong, Edward Elric lost his arm and his leg, and his brother Alphonse became nothing but a soul in a suit of armor. Equipped with mechanical "auto-mail" limbs, Edward becomes a state alchemist, seeking the one thing that can restore his brother and himself... the legendary Philosopher''s Stone.There are many types of alchemy in the world. Edward''s commanding officer, "Flame Alchemist" Roy Mustang, can control fire. Shou Tucker, the "Sewing-Life Alchemist," specializes in the most difficult alchemy of all...biological transmutation, the ability to alter the tissue of living things. But no matter what their power, alchemists are still human. And when a mysterious killer begins stalking state alchemists, no one can escape his vengeance...', '9781591169239', 'English');
