INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (478, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'NARUTO -ナルト- イタチ真伝 暗夜篇 [Naruto: Itachi Shinden — An''ya-hen]', 'Masashi Kishimoto (Original Creator, Illustrator), Takashi Yano (Story)', 4.56, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1443632307l/26816090._SY475_.jpg', '大人気ナルト秘伝小説全6冊に続き、イタチとサスケの真実の物語が3ヶ月連続で発売！　天才とよばれたひとりの忍の栄光と挫折、そして決断の物語。イタチの暗夜行が明かされる、衝撃の第2弾！', '9784087033809', 'Japanese');
