INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (662, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'Master and Commander: 20 Volume Set', 'Patrick O''Brian', 4.76, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1323533133l/1138034.jpg', 'Comprising:Master & Commander;Post Captain;H.M.S. ''Surprise'';The Mauritius Command;Desolation Island;The Fortune of War;The Surgeon''s Mate;The Ionian Mission;Treason''s Harbour;The Far Side of the World;The Reverse of the Medal;The Letter of Marque;The Thirteen-Gun Salute;The Nutmeg of Consolation;The Truelove/Clarissa Oakes;The Wine-Dark Sea;The Commodore;The Yellow Admiral;The Hundred Days;Blue at the Mizzen.', '9780999913499', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (663, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'The End of Olympus', 'Kate O''Hearn', 4.58, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1525226479l/28534579.jpg', 'Emily and Pegasus face their greatest challenge yet when they venture back to Earth to save a friend in this sixth and final book of an exciting series that puts a modern thrill into ancient mythology.After the events in Hawaii, Emily is contending with diminished powers, a new body, and the fact that she has to teach the Titan Lorin how to use her own powers. To make matters worse, Joel has been acting strangely towards her ever since she changed, and it’s all become too much.However, Emily has one last promise to fulfill, one she made a long time ago: to save Agent B from the secret government agency called the CRU. But when Emily, Pegasus, Joel, and Paelen arrive in London, they discover that Agent B has been captured by the CRU and the only way to free him is for Emily and Lorin to surrender.As Emily and her friends delve deeper into the CRU’s history, horrible discoveries are made. Not only about the victims the powerful agency has been trapping and abusing for centuries, but about the very origins of the secret agency itself.Origins that lead directly back to...Emily.', '9999999999999', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (664, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'The New Homeowner''s Guide to House Spirits', 'Alexei Maxim Russell (Goodreads Author)', 4.63, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1414891656l/23489268.jpg', 'A complete guide to those invisible beings, which occupy every home. The result of a life-time of research, this handy manual gives the new home owner the complete picture of that mysterious topic, which your real estate agent could never have explained - the realm of house spirits. House spirits come in all shapes and sizes and have a range of influences on your home and loved ones. With this book, you can learn how to attract only the best house spirits and avoid the bad ones. A must read for the new home owner!', '9781501037139', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (665, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'The Inheritance Cycle', 'Christopher Paolini (Goodreads Author), Joannis Stefanidis (Translator), Michaela Link (Translator)', 4.50, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1333577002l/10859323.jpg', 'The much-anticipated worldwide bestselling Inheritance cycle.', '9780307930675', 'English');
INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (667, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'Give Me Perfect Love', 'Paige P. Horne (Goodreads Author)', 4.58, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1449939854l/28180434._SY475_.jpg', '*This edition is out of print. Find the revised, updated edition here: Give Me Perfect Love*After receiving word that her stepfather has passed away, Kathrine Harrison leaves Atlanta, Ga to return to a town she only remembers as a nightmare. Abandoning a broken Bryce behind to deal with the destruction he’s caused.There’s nothing but chemistry between the two, but they can’t seem to get the science right. Passion and lust only get you so far and once they decided to be more than friends, things only grew deeper.Bryce Grant is damaged, but he’s not letting his past destroy his future. He goes after his girl, desperate to win her back.But Kathrine gets more than she bargained for while visiting her ten-cent town. She runs into someone she thought she’d never see again and finds out truths she never expected. Will she lean on Bryce when she’s at her lowest or will she break, and push the one good thing in her chaotic life away?', 'B019926BVQ', 'English');
