INSERT INTO book (id, created_on, last_updated_on, title, author, rating, image_url, description, isbn, language) VALUES (986, '2023-11-26 22:06:00.241785', '2023-11-26 22:06:00.241785', 'Pegasus and the Rise of the Titans', 'Kate O''Hearn', 4.49, 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1414580877l/23473284.jpg', 'Join Emily and Pegasus as the legend continues in a new epic adventure ...The ancient rivalry between the Olympians and the Titans, thought long over, is rekindled - but this time the Titans have a secret weapon that can rival the power of the Flame of Olympus. The balance of power is tipped in Saturn''s favour now that he has found his own Flame of Titus. Jupiter''s Olympus will finally be in his grasp!Caught in the middle of this ancient power struggle, Emily and Pegasus must head to the Diamond Head volcano in Hawaii to track down the one thing that can save Olympus ... before the Titans get there first.', '9781444922387', 'English');
